var url = 'http://localhost/~maulayyacyber/nyimakid/'

// function video baru
$(document).ready(function(){
    video_baru(0);

    $("#load_more").click(function(e){
        e.preventDefault();
        var page = $(this).data('val');
        video_baru(page);

    });
    //video_baru();
});

var video_baru = function(page){
    $("#loader").show();
    $("#load_more").show();
    $.ajax({
        url: url+"home/get_videos_terbaru",
        type:'GET',
        data: {page:page}
    }).done(function(response){
        $("#videos_terbaru").append(response);
        $("#loader").hide();
        $('#load_more').data('val', ($('#load_more').data('val')+1));
        //scroll();
        if(response == ""){
            $("#load_more").hide();
        }
    });

};

